mapper.select:
SELECT count(*)
FROM MEMBERS
WHERE login_id = #{loginId}

mapper.update:
UPDATE MEMBERS
SET password = #{password}, updated_at = NOW()
WHERE login_id = #{loginId}

UPDATE MEMBERS
SET nick_name = #{nickName}, updated_at = NOW()
WHERE login_id = #{loginId}

UPDATE MEMBERS
SET email = #{email}, updated_at = NOW()
WHERE login_id = #{loginId}
