mapper.select:
SELECT card_name, card_nickname, cardAmount
FROM STAR_CARD
WHERE login_id = #{id}

SELECT member_id, name, login_id, password, tel, email, status, birth, created_at, updated_at, nick_name, gender
FROM MEMBERS
WHERE login_id = #{loginId}

SELECT count(*)
FROM MEMBERS
WHERE login_id = #{loginId}

mapper.update:
UPDATE MEMBERS
SET password = #{password}, updated_at = NOW()
WHERE login_id = #{loginId}

UPDATE MEMBERS
SET nick_name = #{nickName}, updated_at = NOW()
WHERE login_id = #{loginId}

UPDATE MEMBERS
SET email = #{email}, updated_at = NOW()
WHERE login_id = #{loginId}
